    private void bt_InicarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_InicarActionPerformed
        // TODO add your handling code here:
        Roles roles = new Roles();
        roles.setSize(1000,600);
        roles.setLocation(0, 0);
        this.getContentPane().removeAll();
        this.getContentPane().add(roles);
        this.getContentPane().revalidate();
        this.getContentPane().repaint();
    }//GEN-LAST:event_bt_InicarActionPerformed

