    private void bt_CrearMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_bt_CrearMouseClicked
        // TODO add your handling code here:
        try {
            long dpi = Long.parseLong(txt_DPI.getText());
            String nombre = txt_Nombre.getText();
            String apellido = txt_Apellido.getText();
            String telefono = txt_Telefono.getText();
            String direccion = txt_Direccion.getText();
            String password = txt_password.getText();
            String rol = (String) com_Rol.getSelectedItem();
            String sucursal = (String) com_Sucursal.getSelectedItem();
            
            int idSucursal = Utils.obtenerIdSucursal(sucursal);
            int idRol = Utils.obtenerIdRol(rol);
            
            Empleado emp = new Empleado(dpi, nombre, apellido, password, telefono, direccion, idSucursal, idRol);
            EmpleadoDao dao = new EmpleadoDao();
            dao.crear(emp);
            estado.setText("Empleado creado");
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "El DPI debe ser un numero valido");
            estado.setText("El DPI debe ser un numero valido");
        }
    }//GEN-LAST:event_bt_CrearMouseClicked

